import React, { useEffect } from 'react';

export const Blink = (props) => {

    useEffect(()=>{
        this.props.hide = true;
        setInterval(()=>{
            this.props.hide = !this.props.hide;
        },250);
    }, [])

    return(
    <div className={this.props.hide ? 'hide': ''}>{this.props.name}</div>
    )
}
